class Telefone:
    def __init__(self, id: str, number: str):
        self.id = id
        self.number = number

    def getId(self):
        return self.id

    def getNumber(self):
        return self.number

    def __str__(self):
        return f"{self.id}:{self.number}"

    def isvalido(self) -> bool:
        if not self.id or not self.number:
            return False
        if any(c.isalpha() for c in self.number):
            return False
        return True

class Contato:
    def __init__(self, name: str):
        self.name = name
        self.fone: list[Telefone] = []
        self.fav: bool = False

    def getName(self):
        return self.name

    def getFone(self):
        return self.fone

    def setName(self, name: str):
        self.name = name

    def __str__(self):
        fone = ", ".join([str(x)for x in self.fone])
        if self.fav is True:
            return f"@ {self.name} [{fone}]"
        else:
            return f"- {self.name} [{fone}]"

    def adfone(self, id: str, number: str):
        fone = Telefone(id, number)
        if not fone.isvalido():
            print("fail: invalid number")
            return
        self.fone.append(fone)

    def removerfone(self, index: int):
        self.fone.pop(index)

    def colocarfavorito(self):
        if self.fav == False:
            self.fav = True
            return
        else:
            self.fav = False

    def tafavoritado(self) -> bool:
        if self.fav == True:
            return True
        else:
            return False


def main():
    contact = Contato("")
    while True:
        line = input()
        print("$" + line)
        args: list[str] = line.split()

        if args[0] == "end":
            break
        elif args[0] == "show":
            print(contact)
        elif args[0] == "init":
            name = args[1]
            contact.setName(name)
        elif args[0] == "add":
            id = args[1]
            number = args[2]
            contact.adfone(id, number)
        elif args[0] == "rm":
            index = int(args[1])
            contact.removerfone(index)
        if args[0] == "tfav":
            contact.colocarfavorito()
main()
